var logger = require('log4js');

logger.configure({
    appenders: [
        { type: 'console' },
        { type: 'file', filename: 'log/app.log', "maxLogSize": 20480,
            "backups": 20,category: 'app' }
    ]
});

exports.app  = logger.getLogger("app");

exports.request = logger.getLogger("request");
