exports.mixin = function (obj1, obj2/*[,obj3,obj4]*/) {
    var args = Array.prototype.slice.call(arguments);
    if (args.length < 1)
        return {};
    if (args.length == 1)
        return args[0];

    var returnObj = args[0];
    var o = null;
    for (var i = 1, len = args.length; i < len; i++) {
        o = args[i];
        for (var key in o) {
            if (o.hasOwnProperty(key)) {
                returnObj[key] = o[key];
            }
        }
    }
    return returnObj;
}


exports.copy = function (obj) {
    obj = obj || {};
    var result = {};
    for (var key in obj) {
        result[key] = obj[key];
    }
    return result;
}

exports.arrayContainsStr = function (array, str) {
    array = array || [];

    for (var i = 0; i < array.length; i++) {
        var arrayItem = array[i];
        if (arrayItem == str) {
            return true;
        }
    }
    return false;
}